gs -sDEVICE=pdfwrite    -dCompatibilityLevel=1.4    -dNOPAUSE -dQUIET -dBATCH    -dColorImageDownsampleType=/Bicubic    -dGrayImageDownsampleType=/Bicubic    -dMonoImageDownsampleType=/Subsample    -dColorImageResolution=300    -dGrayImageResolution=300    -dMonoImageResolution=300    -dDownsampleColorImages=true    -dDownsampleGrayImages=true    -dDownsampleMonoImages=true    -dEncodeColorImages=true    -dEncodeGrayImages=true    -dEncodeMonoImages=true    -sOutputFile=output.pdf $1
